sys_transform_entry.source_script:
answer = (function transformEntry(source) {

    var gr_country = new GlideRecord('core_country');
    gr_country.addEncodedQuery('iso3166_2=' + source.bank_country + '^ORiso3166_3=' + source.bank_country);
    gr_country.query();
    if (gr_country.next()) {
        return gr_country.getUniqueValue();
    }

})(source);
