sys_transform_entry.source_script:
answer = (function transformEntry(source) {

    var street = source.street_and_house_number;
    var city = source.district_1;
    var state = source.region_state_province_county;
    var zip = source.postal_code;
    var name = new IntegrationsUtils().getLocationName(street, city, state, zip);
    return name;

})(source);
