sys_transform_entry.source_script:
answer = (function transformEntry(source) {

    var location_details = {};
    location_details['street'] = source.street_and_house_number;
    location_details['city'] = source.district_1;
    location_details['state'] = source.region_state_province_county;
    location_details['zip'] = source.postal_code;
    location_details['country'] = source.country_key;
    var name = new IntegrationsUtils().getLocationName(location_details.street, location_details.city, location_details.state, location_details.zip);
    location_details['name'] = name;

    var getLocSysId = new IntegrationsUtilsSNC().getLocationID(location_details);
    if (gs.nil(getLocSysId)) {
        location_details['fax_phone'] = source.fax_number;
        location_details['phone'] = source.first_telephone_number;
        var createLocSysId = new IntegrationsUtilsSNC().createLocation(location_details);
        return createLocSysId.sys_id;
    } else {
        return getLocSysId;
    }

})(source);
